#include <stdio.h>
#include <string.h>
#include <stdbool.h>

#define INPUT_SIZE 4096
#define MIN 0
#define MAX 1

// First answer: 1271 X
// Second answer: 1275 V

int	main(void)
{
	// char		input[] = "mjqjpqmgbljsphdztnvjfqwrcgsmlb";
	char		input[INPUT_SIZE] = "";
	int			i = 0;


	printf(	"---------------------------\n"
			"Advent of Code 2022: Day 6 Part One!\n"
			"---------------------------\n");
	while (input[i] != '\0')
	{
		printf("Checking characters ['%c', '%c', '%c', '%c']...\n", input[i], input[i + 1], input[i + 2], input[i + 3]);
		if (input[i] != input[i + 1] && input[i] != input[i + 2] && input[i] != input[i + 3] &&
		input[i + 1] != input[i + 2] && input[i + 1] != input[i + 3] && input[i + 2] != input[i + 3])
		{
			printf("\tFound string %*s\n", i + 1, input);
			break ;
		}
		i++;
	}
	printf("%i is the answer\n", i + 4);
	return (0);
}
